mapper.select:
	select jj.JJDBH, jj.AJZT, jj.BJDH, jj.SFDZ, jj.BJDHYHXM, jj.BJFSBH,jj.CASE_LEVEL,jj.GXDWBH,
	jj.BJNR,jj.BJSJ,jj.BJLB,jj.BJLX,jj.MARK,atb.TYPE_NAME as BJLBMC,atx.TYPE_NAME as BJLXMC
	from b_jjdb jj
	left join b_alarm_type atb on jj.BJLB = atb.TYPE_CODE
	left join b_alarm_type atx on jj.BJLX = atx.TYPE_CODE
	where 1=1 
	
		AND jj.AJZT in
		  
        #{item}   
    
	
	
		AND jj.BJDH = #{bjdh,jdbcType=VARCHAR}
	
	
		AND jj.CASE_LEVEL = #{caseLevel,jdbcType=INTEGER}
	
	
		AND jj.BJLB in 
		  
        #{item}   
    
	
	
		AND jj.BJLX in
		  
        #{item}   
    
	
	
		AND jj.BJXL in 
		  
        #{item}   
    
	
	
		AND jj.SFDZ LIKE CONCAT ('%',#{sfdz,jdbcType=VARCHAR},'%' )
	
	
		AND jj.MARK = #{alarmLocation,jdbcType=INTEGER}
	
	
		AND (jj.MARK = #{alarmLocation,jdbcType=INTEGER} OR jj.MARK is null)
	
	
		AND  jj.BJSJ >= #{startTime,jdbcType=TIMESTAMP} 
	
	
		AND   jj.BJSJ <= #{endTime,jdbcType=TIMESTAMP} 
	
	
		AND jj.GXDWBH in 
		  
        #{item}   
    
	
	
		AND jj.BJNR LIKE CONCAT ('%',#{keyWord,jdbcType=VARCHAR},'%' )
	
	order by jj.BJSJ desc
