mapper.select:
select      rid,     name,     data_type,id,code ,orgName
from 
 (    
	select    ORGAN_ID as rid,short_name as name,  1 as data_type,CONCAT("org_",ORGAN_ID) as id,code ,short_name as orgName from t_organ as org    where  org.parent_id= #{id,jdbcType=INTEGER}  
	 
	union    
	select     id as rid, CONCAT(p.name,"(",number,")") as name, 2 as data_type,CONCAT("pol_",id) as id,p.number as code , og.short_name as orgName   from t_police as p    
	
		left join t_organ as og on og.organ_id = p.org_id
	where p.isUsed = 1 and p.org_id=#{id,jdbcType=INTEGER}  
 ) as t    
order by t.data_type desc,CONVERT(t.name USING gbk),t.rid

select rid, typename, name, data_type,id,code ,orgName from ( select ORGAN_ID as rid,"" as typename,short_name as name, 1 as data_type,CONCAT("org_",ORGAN_ID) as id,code,short_name as orgName from t_organ as org where org.parent_id= #{id,jdbcType=INTEGER}
union select p.id as rid,t.name as typename,CONCAT(t.name,"(",p.number,")") as name, 2 as data_type,CONCAT("veh_",p.id) as id,p.number as code , og.short_name as orgName from t_vehicle as p left join t_vehicletype as t on t.id = p.vehicle_type_id left join t_organ as og on og.organ_id = p.org_id
where p.org_id=#{id,jdbcType=INTEGER} ) as t order by t.data_type desc,CONVERT(t.name USING gbk),t.rid

select rid, typename, name, data_type,id,code ,orgName from ( select ORGAN_ID as rid,"" as typename,short_name as name, 1 as data_type,CONCAT("org_",ORGAN_ID) as id,code,short_name as orgName from t_organ as org where org.parent_id= #{id,jdbcType=INTEGER}
union select p.id as rid,t.name as typename, CONCAT(t.name,"(",p.number,")") as name, 2 as data_type,CONCAT("wea_",p.id) as id,p.number as code, og.short_name as orgName from t_weapon as p left join t_weapontype as t on t.id = p.type_id left join t_organ as og on og.organ_id = p.org_id
where p.org_id=#{id,jdbcType=INTEGER} ) as t order by t.data_type desc, CONVERT(t.name USING gbk),t.rid

select rid, typename, name, data_type,id,code ,orgName from ( select org.ORGAN_ID as rid,"" as typename,short_name as name, 1 as data_type,CONCAT("org_",org.ORGAN_ID) as id,code,short_name as orgName from t_organ as org where org.parent_id= #{id,jdbcType=INTEGER}
union
select p.id as rid,gt.name as typename,CONCAT(gt.name,"(",p.gps_name,":",p.number,")") as name , 2 as data_type,CONCAT("gps_",p.id) as id,p.number as code ,og.short_name as orgName from t_gps as p left join t_gpstype as gt on gt.id = p.type_id left join t_organ as og on og.organ_id = p.org_id where p.org_id=#{id,jdbcType=INTEGER} ) as t order by t.data_type desc,CONVERT(t.name USING gbk),t.rid
