mapper.select:
select 

from USERS U
where ID = #{id,jdbcType=DECIMAL}

mapper.update:
UPDATE USERS U 
SET U.PASSWORD = #{password,jdbcType=VARCHAR} 
WHERE U.ID = #{id, jdbcType=DECIMAL}

mapper.select:
select count(*) from USERS U 
where U.USER_ID = #{userId,jdbcType=VARCHAR}

	AND U.ID != #{id,jdbcType=DECIMAL}
