mapper.insert:
insert into t_case_report ( CODE, cdate, cyear, cmonth, cday, chour, ymd, ym, org_id, org_code, org_path, org_parent_id, state, phone, clevel, type1, type2, type3, content, address, gps, operator, update_time ) SELECT bj.JJDBH, bj.BJSJ, YEAR (bj.BJSJ), MONTH (bj.BJSJ), DAY (bj.BJSJ), HOUR (bj.bjsj), DATE_FORMAT(bj.BJSJ, '%Y%m%d'), DATE_FORMAT(bj.BJSJ, '%Y%m'), org.ORGAN_ID, org.CODE, org.PATH, org.parent_id, bj.AJZT, bj.LXDH, bj.CASE_LEVEL, bj.BJLB, bj.BJLX, bj.BJXL, bj.BJNR, bj.SFDZ, bj.GPS_CONFIG, bj.JJYXM, bj.update_time FROM b_jjdb AS bj LEFT JOIN t_organ AS org ON bj.gxdwbh = org.CODE WHERE not exists(select 1 from t_case_report as cr where cr.code=bj.jjdbh )
and bj.update_time >=#{beginTime,jdbcType=TIMESTAMP}
and bj.update_time < #{endTime,jdbcType=TIMESTAMP}
order by bj.JJDBH

mapper.update:
update t_case_report as cr INNER JOIN ( select bj.JJDBH, bj.BJSJ, bj.AJZT, bj.LXDH, bj.CASE_LEVEL, bj.BJLB, bj.BJLX, bj.BJXL, bj.BJNR, bj.SFDZ, bj.GPS_CONFIG, bj.JJYXM, bj.update_time, org.organ_id, org.code, org.parent_id, org.path FROM b_jjdb as bj INNER JOIN t_organ as org on bj.gxdwbh=org.code where EXISTS (select 1 from t_case_report as c where c.code =bj.jjdbh and c.update_time < bj.update_time)
and bj.update_time >=#{beginTime,jdbcType=TIMESTAMP}
and bj.update_time < #{endTime,jdbcType=TIMESTAMP}
) as t on t.jjdbh=cr.code SET cr.code =t.JJDBH, cdate =t.BJSJ, cyear =YEAR (t.BJSJ), cmonth =MONTH (t.BJSJ), cday =DAY (t.BJSJ), chour =HOUR (t.bjsj), ymd =DATE_FORMAT(t.BJSJ, '%Y%m%d'), ym =DATE_FORMAT(t.BJSJ, '%Y%m'), org_id =t.ORGAN_ID, org_code =t.CODE, org_path =t.PATH, org_parent_id =t.parent_id, state =t.AJZT, phone =t.LXDH, clevel =t.CASE_LEVEL, type1 =t.BJLB, type2 =t.BJLX, type3 =t.BJXL, content =t.BJNR, address =t.SFDZ, gps =t.GPS_CONFIG, operator =t.JJYXM, cr.update_time =t.update_time
